import { useState, useEffect } from 'react';

function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    console.log('I am here');
    async function fetchData() {
      console.log('Ashish');
      const data = await fetch('https://covid19.mathdro.id/api').then(res =>
        res.json()
      );

      setStats(data);
    }

    fetchData();
  }, []);

  return stats;
}

function Stats() {
  const stats = useStats();

  if (!stats) return <p> Loading.....</p>;
  console.log('I am here ', stats);
  return (
    <div>
      <div>
        <h1> Confirmed</h1>
        <p>{stats.confirmed.value}</p>
      </div>
      <div>
        <h1> Deadths</h1>
        <p>{stats.deaths.value}</p>
      </div>
      <div>
        <h1> Recovered</h1>
        <p>{stats.recovered.value}</p>
      </div>
    </div>
  );
}

export default function IndexPage() {
  return (
    <div>
      <Stats></Stats>
    </div>
  );
}
